mapper.select:
SELECT

FROM ft_product p

WHERE p.del_flag = #{delFlag}

    AND u.id = #{user.id}


    AND p.is_season = #{isSeason}


    AND p.is_promotion = #{isPromotion}

ORDER  BY  p.create_time DESC
limit #{page.begin},#{page.size}

SELECT COUNT(1) FROM ft_product p

WHERE p.del_flag = #{delFlag}

    AND u.id = #{user.id}


    AND p.is_season = #{isSeason}


    AND p.is_promotion = #{isPromotion}

SELECT

FROM ft_product p

WHERE p.id = #{id}
