mapper.select:
SELECT

FROM ft_product_sales ps

WHERE ps.del_flag = '0'

    AND b.user_id = #{userId}


    AND ps.product_id = #{productId}

ORDER  BY  ps.create_time DESC
limit #{page.begin},#{page.size}

SELECT COUNT(1) FROM ft_product_sales ps

WHERE ps.del_flag = '0'

    AND b.user_id = #{userId}


    AND ps.product_id = #{productId}
