mapper.select:
SELECT , group_concat(r.description) AS roleNames,group_concat(r.id) AS roleIdsStr FROM sys_user u
WHERE u.del_flag = '0'
AND u.username like CONCAT('%', #{username}, '%')
AND u.telephone = #{mobile}
GROUP BY u.username ORDER BY u.id DESC limit #{page.begin},#{page.size}

SELECT COUNT(1) FROM sys_user u WHERE u.del_flag = '0'
AND u.username like CONCAT('%', #{username}, '%')
AND u.telephone = #{mobile}

mapper.update:
UPDATE sys_user SET
username = #{username},
telephone = #{telephone},
real_name = #{realName},
identity_card_num = #{identityCardNum}
WHERE id = #{id}

UPDATE sys_user SET
	password = #{password},
	salt = #{salt}
WHERE id = #{id}

UPDATE sys_user SET
	login_ip = #{loginIp},
	login_Date = #{loginDate}
WHERE id = #{id}

UPDATE sys_user SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
