mapper.select:
SELECT , group_concat(r.name_zh) AS roleNames,group_concat(r.id) AS roleIdsStr FROM t_sys_user u
WHERE u.del_flag = '0'
AND u.username like CONCAT('%', #{username}, '%')
AND u.telephone = #{mobile}
GROUP BY u.id, u.username, u.password, u.real_name, u.photo, u.add_time, u.telephone, u.identity_card_num, u.login_ip, u.login_date, u.remarks, u.salt, u.locked, u.create_by, u.create_date, u.update_by, u.update_date, u.del_flag ORDER BY u.id DESC limit #{page.begin},#{page.size}

SELECT COUNT(1) FROM t_sys_user u WHERE u.del_flag = '0'
AND u.username like CONCAT('%', #{username}, '%')
AND u.telephone = #{mobile}

mapper.update:
UPDATE t_sys_user SET
username = #{username},
telephone = #{telephone},
real_name = #{realName},
identity_card_num = #{identityCardNum}
WHERE id = #{id}

UPDATE t_sys_user SET
	password = #{password},
	salt = #{salt}
WHERE id = #{id}

UPDATE t_sys_user SET
	login_ip = #{loginIp},
	login_Date = #{loginDate}
WHERE id = #{id}

UPDATE t_sys_user SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
